#!/bin/bash
exec > >(tee /var/log/user-data.log|logger -t user-data -s 2>/dev/console) 2>&1
echo 'Starting user data script'
yum update -y
echo 'Installing git'
yum install -y git
echo 'Installing Node.js'
curl -fsSL https://rpm.nodesource.com/setup_18.x | bash -
yum install -y nodejs
echo 'Node.js version:'
node --version
npm --version
echo 'Cloning repository'
cd /home/ec2-user
git clone https://github.com/jwehlen-cell/BitcoinTracker.git
cd BitcoinTracker
echo 'Installing npm dependencies'
npm install
echo 'Creating systemd service'
cat > /etc/systemd/system/bitcoin-tracker.service << 'EOF'
[Unit]
Description=Bitcoin Mining Tracker
After=network.target

[Service]
Type=simple
User=ec2-user
WorkingDirectory=/home/ec2-user/BitcoinTracker
ExecStart=/usr/bin/node server.js
Restart=always
Environment=NODE_ENV=production
Environment=PORT=3000

[Install]
WantedBy=multi-user.target
EOF
systemctl daemon-reload
systemctl enable bitcoin-tracker
systemctl start bitcoin-tracker
echo 'Installing and configuring nginx'
yum install -y nginx
cat > /etc/nginx/nginx.conf << 'EOF'
user nginx;
worker_processes auto;
error_log /var/log/nginx/error.log;
pid /run/nginx.pid;

events {
    worker_connections 1024;
}

http {
    log_format main '$remote_addr - $remote_user [$time_local] "$request" '
                    '$status $body_bytes_sent "$http_referer" '
                    '"$http_user_agent" "$http_x_forwarded_for"';

    access_log /var/log/nginx/access.log main;

    sendfile on;
    tcp_nopush on;
    tcp_nodelay on;
    keepalive_timeout 65;
    types_hash_max_size 2048;

    include /etc/nginx/mime.types;
    default_type application/octet-stream;

    server {
        listen 80 default_server;
        listen [::]:80 default_server;
        server_name _;

        location / {
            proxy_pass http://127.0.0.1:3000;
            proxy_http_version 1.1;
            proxy_set_header Upgrade $http_upgrade;
            proxy_set_header Connection 'upgrade';
            proxy_set_header Host $host;
            proxy_set_header X-Real-IP $remote_addr;
            proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
            proxy_set_header X-Forwarded-Proto $scheme;
            proxy_cache_bypass $http_upgrade;
        }
    }
}
EOF
systemctl enable nginx
systemctl start nginx
echo 'Checking service status'
systemctl status bitcoin-tracker
systemctl status nginx
echo 'User data script completed'
